/*
  Do not copy/paste this code. It is used internally
  to manage end-to-end test suites.
*/

const NextI18Next = require('next-i18next').default
const {
	localeSubpaths
} = require('next/config').default().publicRuntimeConfig

const localeSubpathVariations = {
	none: {},
	foreign: {
		de: 'de',
	},
	all: {
		en: 'en',
		de: 'de',
	},
}

const def = new NextI18Next({
	defaultLanguage: 'en',
  otherLanguages: ['de'],
  load: localeSubpathVariations[localeSubpaths],
	// localePath: 'static/locales',
  // localeStructure: LOCALE_STRUCTURE,
  // localeExtension: LOCALE_EXTENSION,
  localeSubpaths: localeSubpathVariations[localeSubpaths],
  // use: [],
  // defaultNS: DEFAULT_NAMESPACE,
  // interpolation: {
  //   escapeValue: false,
  //   formatSeparator: ',',
  //   format: (value, format) => (format === 'uppercase' ? value.toUpperCase() : value),
  // },
  browserLanguageDetection: true,
  serverLanguageDetection: true,
  ignoreRoutes: ['/_next/', '/static/', '/public/', '/api/'],
  customDetectors: [],
  detection: {
    lookupCookie: 'next-i18next',
		order: ['htmlTag'],
    // order: ['cookie', 'header', 'querystring'],
    // caches: ['cookie'],
		// lookupQuerystring: false,
		// lookupSession: false,
		// lookupFromPathIndex: 0,
  },
  react: {
    wait: true,
    useSuspense: false,
  },
  strictMode: true,
  errorStackTraceLimit: 0,
  shallowRender: false,
	// debug: process.env.I18N_DEBUG === '1'


})


console.warn('='.repeat(100));
console.warn(def);
console.warn(def.config.detection.order);

module.exports = def
